# Long-running build with extended timeout
try {
    Invoke-WithTimeout {
        cmake --build . --config Release
    } -TimeoutSeconds 300 | Find-Errors
} catch [System.TimeoutException] {
    Write-Error "Build exceeded 5 minute timeout"
    exit 1
}

